(4350, 336, '_about_culture_repeater_0_culture_content', 'field_66e274e58164f'),
(4351, 336, 'about_culture_repeater_1_culture_title', 'Our USP'),
(4352, 336, '_about_culture_repeater_1_culture_title', 'field_66e274568164e'),
(4353, 336, 'about_culture_repeater_1_culture_content', 'Our USP lies in delivering tailored IT solutions that combine cutting-edge technology with personalized support. We offer fast, reliable service, ensuring seamless operations for our clients. Our expert team goes beyond the standard to provide proactive and innovative solutions that drive business growth.'),
(4354, 336, '_about_culture_repeater_1_culture_content', 'field_66e274e58164f'),
(4355, 336, 'about_culture_repeater_2_culture_title', 'Business Model'),
(4356, 336, '_about_culture_repeater_2_culture_title', 'field_66e274568164e'),
(4357, 336, 'about_culture_repeater_2_culture_content', 'Our business model is centered on providing flexible, scalable IT services tailored to client needs. We offer a mix of on-demand support, managed services, and custom solutions, ensuring seamless technology integration. By focusing on innovation and customer satisfaction, we deliver value through proactive and reliable IT management.'),
(4358, 336, '_about_culture_repeater_2_culture_content', 'field_66e274e58164f'),
(4359, 336, 'about_culture_repeater_3_culture_title', 'Our CSR'),
(4360, 336, '_about_culture_repeater_3_culture_title', 'field_66e274568164e'),
(4361, 336, 'about_culture_repeater_3_culture_content', 'Our CSR initiatives focus on leveraging technology to support education, environmental sustainability, and community well-being. We invest in programs that promote digital literacy, reduce our carbon footprint, and foster diversity and inclusion. Through partnerships and employee engagement, we aim to make a positive impact locally and globally.'),
(4362, 336, '_about_culture_repeater_3_culture_content', 'field_66e274e58164f'),
(4363, 336, 'about_culture_repeater_4_culture_title', 'Our Achievements'),
