(12527, 655, '_about_culture_repeater_2_culture_content', 'field_66e274e58164f'),
(12528, 655, 'about_culture_repeater_3_culture_title', 'Our CSR'),
(12529, 655, '_about_culture_repeater_3_culture_title', 'field_66e274568164e'),
